# Final optimization test with more photos and wider columns
$jsonData = @{
  "title"          = "Optimized Excel Test - Column Overflow Protection Test"
  "companyInfo"    = @{
    "name"    = "Test Company Ltd"
    "address" = "Very long address that might span multiple lines and test column boundaries"
    "phone"   = "555-123-456"
    "email"   = "test@company.ge"
  }
  "assessmentDate" = "2025-01-08"
  "assessor"       = "Test Assessor for Optimization"
  "hazards"        = @(
    @{
      "id"              = "1"
      "title"           = "Very Long Hazard Title That Should Test Column Boundaries and Overflow Protection Mechanisms"
      "description"     = "Detailed risk description that is also very long to test text wrapping and column management"
      "riskLevel"       = "High"
      "recommendations" = "Risk mitigation recommendations"
      "photos"          = @(
        "data:image/jpeg;base64,",
        "data:image/jpeg;base64,",
        "data:image/jpeg;base64,",
        "data:image/jpeg;base64,",
        "data:image/jpeg;base64,",
        "data:image/jpeg;base64,"
      )
    }
    @{
      "id"              = "2" 
      "title"           = "Second Hazard With Also Long Title"
      "description"     = "This is the second hazard description"
      "riskLevel"       = "Medium"
      "recommendations" = "Second risk recommendations"
      "photos"          = @(
        "data:image/jpeg;base64,",
        "data:image/jpeg;base64,"
      )
    }
    @{
      "id"              = "3"
      "title"           = "Third Hazard For Column Boundary Testing - Very Long Title That Should Test Column Overflow Protection"
      "description"     = "This is a particularly long description for the third hazard that will be with more text"
      "riskLevel"       = "Low"
      "recommendations" = "Very detailed recommendations that might be multi-line and comprehensive"
      "photos"          = @(
        "data:image/jpeg;base64,"
      )
    }
  )
} | ConvertTo-Json -Depth 5

Write-Host "Testing optimized Excel generation with wide columns and many photos..."
Write-Host "Data includes hazards with column overflow protection test"

Invoke-RestMethod -Uri "http://localhost:10000/api/documents/generate-excel" `
  -Method POST `
  -Body $jsonData `
  -ContentType "application/json" `
  -OutFile "test_optimized_final.xlsx"

if (Test-Path "test_optimized_final.xlsx") {
  $fileSize = (Get-Item "test_optimized_final.xlsx").Length
  Write-Host "Excel file generated successfully!"
  Write-Host "File: test_optimized_final.xlsx"
  Write-Host "Size: $fileSize bytes"
    
  # Try to open with Excel if available
  if (Get-Command "excel" -ErrorAction SilentlyContinue) {
    Write-Host "Opening Excel file..."
    Start-Process "excel" -ArgumentList "test_optimized_final.xlsx"
  }
  else {
    Write-Host "Excel not found, file saved at: $(Get-Location)\test_optimized_final.xlsx"
  }
}
else {
  Write-Host "Excel file generation failed"
}
